/** Normalize Telegram update into one compact shape */
const m = $json.message || {};
const c = $json.callback_query || {};
const isCb = !!c.data;
const chat_id = isCb ? c.message?.chat?.id : m.chat?.id;
const text = (m.text || '').trim();
const data = isCb ? (c.data || '').trim() : '';
const message_id = isCb ? c.message?.message_id : m.message_id;
const username = (m.from?.username || c.from?.username || '') || '';
const cqid = c.id || null;
return [{ json: { chat_id, text, data, is_callback: isCb, message_id, username, cqid, _raw: $json } }];

// Parse callback and derive kind/action/sku/page
const u = $json;
const data = String(u.data || '').trim();
let kind = '', action = '', sku = '';
if (!data && u.text) kind = 'text';
if (data) {
  const parts = data.split('|');
  if (parts.length >= 3) { [kind, action, sku] = parts; }
  else if (parts.length === 2) { [action, sku] = parts; kind = 'BTN'; }
  else { action = parts[0] || ''; kind = 'BTN'; }
}
return [{ json: { ...u, kind, action, sku } }];

